import React, { Component, Card, useState } from "react";
import { List, ListItem } from "../components/List";
import MainNav from "../components/MainNav";
import API from "../utils/API";
import SearchForm from "../components/SearchForm";
import Jumbotron from "../components/Jumbotron";
import HomeSearch from "../components/HomeSearch";


class SearchResults extends Component {

  state = {
    search: "",
    events: [],
  };

  handleInputChange = (event) => {
    this.setState({ search: event.target.value });
  };

  handleFormSubmit = (event) => {
    event.preventDefault();
    API.getEvent(this.state.search).then((res) => {
      console.log("res.data: ", res.data);
      const events = res.data.map((event) => {
        console.log(event.lineup[0]);
        const id = event.id;
        const artist = event.lineup[0];
        const location = event.venue.location;
        const venue = event.venue.name;
        const date = event.datetime;
        const eventUrl = event.url;

        const eventObj = {
          id: id,
          artist: artist,
          location: location,
          venue: venue,
          date: date,
          eventUrl: eventUrl,
        };
        console.log(eventObj);
        return eventObj;
      });
      console.log(events);
      this.setState({ events: events });


    })
    .catch((err) => console.log("API.getEvent err: ", err));
  };

  render() {
    console.log(this.state.events);
    return (
      <div>
        <MainNav />
        <Jumbotron />
        <SearchForm 
        handleFormSubmit={this.handleFormSubmit}
        handleInputChange={this.handleInputChange}
        search={this.state.search}

        />
        <div className="container">
          {this.state.events ? (
            <List className="overflow-container">
              {this.state.events.map((event) => (
                <ListItem key={event.id}>
                  <p to={"/searchresult/" + event.id}>
                    <strong>
                      {event.artist} at {event.venue}
                    </strong>
                  </p>
                </ListItem>
              ))}
            </List>
          ) : (
            <h3>No Results to Display</h3>
          )}
          ;
        </div>
      </div>
    );
  }
}

export default SearchResults;
